import './index.scss';
import AnimatedLetters from "../AnimatedLetters";
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faAngular } from '@fortawesome/free-brands-svg-icons';

const About = () => {

    const [letterClass, setLetterClass] = useState('text-animate');

    return(
        <div className="container about-page">
            <div className="text-zone">
                <h1>
                    <AnimatedLetters  letterClass = {letterClass}
                        strArray={['A','b','o','u','t', ' ', 'M', 'e']} 
                        idx={15}
                    />
                </h1>
                <p>An enthusiastic web developer seeking a job position to continue developing skills in the field of
                web development and to gain experience working in a professional environment.</p>
                <p>Possess 5 years of experience in back-end web development, with proficiency in PHP, Magento 2 CMS, and Laravel 
                framework, as well as experience with NodeJS, and ExpressJs framework, MongoDB, Mongoose.</p>
                <p>I am eager to learn and apply new skills, take on new challenges, 
                and contribute to the success of the company.</p>
            </div>
            <div className='stage-cube-cont'>
                <div className='cubespinner'>
                    <div className='face1'>
                        <FontAwesomeIcon icon={faAngular} color="#DD0031"/>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default About;
